import React, { useState } from "react";
import {Link} from 'react-router-dom'

import "./style.css";
import Menu from "./menuApi";
import MenuCard from "./Menucard";
import Navbar from "./navrbar";
import Footer from "./footer";
import Imgslider from "./Imgslider";
import ScrollToTop from "./gotobtn";
import Search from "./Search"




const uniqueList = [
  ...new Set(
    Menu.map((curElem) => {
      return curElem.category;
    })
  ),
  "All",
];

console.log(uniqueList);

const Boxoffice = () => {
  const [menuData, setMenuData] = useState(Menu);
  const [menuList] = useState(uniqueList);

  const filterItem = (category) => {
    if (category === "All") {
      setMenuData(Menu);
      return;
    }

    const updatedList = Menu.filter((curElem) => {
      return curElem.category === category;
    });

    setMenuData(updatedList);
  };

  return (
    <>
      
      <div   className="bckbtn">
      <Link  className="back-btn btn btn-outline-dark" to='/'>Back</Link>
      </div>
      <div className="site-header sticky-top py-1">
        <nav className="container d-flex flex-column flex-md-row justify-content-between">
          <a className="py-2" href="#" aria-label="Product">
            <div
              width={24}
              height={24}
              fill="none"
              stroke="currentColor"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              className="d-block mx-auto"
              role="img"
              viewBox="0 0 24 24"
            />
          </a>
          <Link className="navlink py-2 d-none d-md-inline-block" to="/">
            Home
          </Link>
          <Link className="navlink py-2 d-none d-md-inline-block" to="/">
            Movies
          </Link>
          <Link className="navlink py-2 d-none d-md-inline-block" to="/About">
            About
           
          </Link>
          
          <a className="navlink py-2 d-none d-md-inline-block" href="mailto:Abhaycbr07@gmail.com">
            Contact
          </a>
        </nav>
      </div>
      <div class="sliderr">Featured Movies</div>
      
      
     
      
      
      <div className="Sliderbg">
      <Imgslider/>
      </div>
      
      <Navbar filterItem={filterItem} menuList={menuList} />
      <Search/>
      <MenuCard menuData={menuData} />
      <ScrollToTop/>
      
      <Footer/>
    </>
  );
};

export default Boxoffice;
