import React, { useState } from "react";

const Navbar = ({ filterItem, menuList }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [selectedSemester, setSelectedSemester] = useState(null);

  const toggleDropdown = () => {
    setShowDropdown((prevState) => !prevState);
  };

  const handleItemClick = (item) => {
    if (item.type === "branch") {
      setSelectedBranch(item.value);
    } else if (item.type === "semester") {
      setSelectedSemester(item.value);
    }

    filterItem({ branch: selectedBranch, semester: selectedSemester });
    setShowDropdown(false);
  };

  const branchMenu = ["CS", "IT", "ET", "EE", "ME", "CE", "CHE", "LE", "PT", "PL", "Ol", "BC"];
  const semesterMenu = [1, 2, 3, 4, 5, 6, 7, 8];

  return (
    <>
      <div id="section1" className="line"></div>
      <nav className="navbarr">
        <div className="navbar-content">
          <div className="explore-categories-btn" onClick={toggleDropdown}>
            Click here : Categories<i className={`arrow ${showDropdown ? "up" : "down"}`}></i>
          </div>
          <div className={`dropdown-menu ${showDropdown ? "show" : ""}`}>
            <div className="dropdown-section">
              <h4>Branch</h4>
              {branchMenu.map((branch) => (
                <button
                  key={branch}
                  className={`btn btn-group__item ${selectedBranch === branch ? "selected" : ""}`}
                  onClick={() => handleItemClick({ type: "branch", value: branch })}
                >
                  {branch}
                </button>
              ))}
            </div>
            <div className="dropdown-section">
              <h4>Semester</h4>
              {semesterMenu.map((semester) => (
                <button
                  key={semester}
                  className={`btn btn-group__item ${selectedSemester === semester ? "selected" : ""}`}
                  onClick={() => handleItemClick({ type: "semester", value: semester })}
                >
                  {semester}
                </button>
              ))}
            </div>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
