import React from "react";

const MenuCard = ({ menuData }) => {
  //   console.log(menuData);
  


  return (
    <>
      <section className="main-card--container">
        {menuData.map((curElem) => {
          const { stars, name, category, image, description, watch } = curElem;


      let countFinal = document.getElementById("count")
  let counts = 0
  function increment() {
    counts += 1
    countFinal.innerText = counts
  }
          return (
            <>
              <div className="card-container" key={stars}>
                <div className="cardd ">
                  <div className="card-body">
                    <span className="card-number card-rectangle subtle">Stars: {stars}</span>
                    <span className="card-author"> {category}</span>
                    <h2 className="card-title"> {name} </h2>
                    <span className="card-description">
                      {description}
                    </span>
                    <h6 id="counts">0</h6>
                    <div className="animate-border"></div><button onClick="increment()" className="vote">Voteüëç</button>
                  </div>
                  <img src={image} alt="images" className="card-media" width="350px" height="200px" />

                  <a target="-blank" className="card-tag" href={watch}>Watch Now</a>
                </div>
              </div>
            </>
          );
        })}
      </section>
    </>
  );
};

export default MenuCard;
