      }}>
      {/* The above code is validating the input and printing it on console */}


      {Formik => (
        <div style={{
          color: "grey"
        }}>

          <form onSubmit={handleSubmit}>
            <div className="container">
              <center>
                <div className="tittlee">
                  <span className="welcomee">Register</span>
                </div>
              </center>
              <hr />

              {/* firstName */}
              <label> First name </label>
              <TextField
                type="text"
                name="firstName"
                placeholder="First name"
                size={15}
                required="" />

              {/* lastName */}
              <label> Last name: </label>
              <TextField
                type="text"
                name="lastName"
                placeholder="Last name"
                size={15}
                required="" />

              {/* email */}
              <label htmlFor="email"><b>Email</b></label>
              <TextField
                type="email"
                name="email"
                placeholder="Enter Email"
                required="" />


              {/* password */}
              <label htmlFor="password"><b>Password</b></label>
              <TextField
                type="password"
                placeholder="Enter Password"
                name="password"
                required="" />


              {/* ReType */}
              <label htmlFor="confirmPassword"><b>Re-type Password</b></label>
              <TextField
                type="password"
                placeholder="Retype Password"
                name="confirmPassword"
                required="" />


              {/* Registerbutton */}
              <button type="submit" className="registerbtn">
                Register
              </button>
              <br />
              {/* back button */}
              <Link to="/" className="btn btn-dark mt-3 ml-3">Back</Link>
            </div>
          </form>
        </div>
      )}
    </Formik >

  )
}

export default Register
