import React, { useState } from "react";
import axios from 'axios';
const MenuCard = ({ menuData }) => {

  //vote
  function VoteButton({ movieId }) {
    const [isVoted, setIsVoted] = useState(false);

    function handleVote() {
      axios.post(`./MovieApi.js/${id}`)
        .then(() => {
          setIsVoted(true);
        })
        .catch((error) => {
          console.error(error);
        });
    }


  }

  const [buttonText, setButtonText] = useState('Voteüëç');
  function handleClick() {
    setButtonText('VotedüòÑ');
  }
  return (
    <>
      <section className="main-card--container">
        {menuData.map((curElem) => {
          const { id, name, category, image, description, watch } = curElem;
          return (
            <>
              <div className="card-container" key={id}>
                <div className="cardd ">
                  <div className="card-body">
                    <span className="card-number card-rectangle subtle">Id: {id}</span>
                    <span className="card-author"> {category}</span>
                    <h2 className="card-title"> {name} </h2>
                    <span className="card-description">
                      {description}
                    </span>
                    <div className="animate-border"></div>
                    <button className="vote" onClick={handleVote}>
                      {isVoted ? 'Voted!' : 'Vote'}
                    </button>
                  </div>
                  <img src={image} alt="images" className="card-media" width="100%" height="200px" />
                  <a target="_blank" className="card-tag" href={watch}>Watch Now</a>
                </div>
              </div>
            </>
          );
        })}
      </section>
    </>
  );
};

export default MenuCard;
