import React from 'react'

const Carousel = () => {
    document.querySelector(".carousel").owlCarousel({
        margin: 20,
        loop: true,
        autoplay: true,
        autoplayTimeout: 2000,
        autoplayHoverPause: true,
        responsive: {
            0: { items: 1, nav: false },
            600: { items: 2, nav: false },
            1000: { items: 3, nav: false },
        },
    });

    return (

        <>
            <div className="wrapper carousell">
                <div className="carousel owl-carousel">
                    <div className="card card-1"> A </div>
                    <div className="card card-2"> B </div>
                    <div className="card card-3"> C </div>
                    <div className="card card-4"> D </div>
                    <div className="card card-5"> E </div>
                </div>
            </div>

        </>
    );
};

export default Carousel;
