import React, { useState } from "react";
import { Routes, Route } from 'react-router-dom';

import "./style.css";
import Menu from "./component/Basics/menuApi";
import MenuCard from "./Menucard";
import Navbar from "./navrbar";
import Header from "./header";
import Footer from "./footer";
import ImageSlider from "./imageslider";
import ScrollToTop from "./gotobtn";
import About1 from "./About1";

//import {Restaurant} from './component/Basics/restaurant'; if u r using export const restaurant in restaurant.js






const App = () => {
  const [menuData, setMenuData] = useState(Menu);
  const [menuList] = useState(uniqueList);

  const filterItem = (category) => {
    if (category === "All") {
      setMenuData(Menu);
      return;
    }

    const updatedList = Menu.filter((curElem) => {
      return curElem.category === category;
    });

    setMenuData(updatedList);
  };

  return (
    <div className="App">
      <Routes>





        <Route path="/" element={<Header />} />



        <div class="sliderr">Upcoming Movies</div>
        <Route path="/about" element={<About1 />} />

        <Route path="/" element={<ImageSlider />} />



        <Route path="/" element={<Navbar filterItem={filterItem} menuList={menuList} />} />
        <Route path="/" element={<MenuCard menuData={menuData} />} />
        <Route path="/" element={<ScrollToTop />} />

        <Route path="/" element={<Footer />} />

      </Routes>








    </div >
  )}

export default App;
