import React , {useState} from "react";
import Menu from "./menuApi";


const MenuCard = ({ menuData }) => {

  
  const [buttonText, setButtonText] = useState('Vote👍');
  function handleClick() {
    setButtonText('Voted😄');
  }
  
  const[filter , setFilter]= useState('');
  const searchText = (e)=>{
    setFilter(e.target.value);
  }

  
  let dataSearch = Menu.cardData.filter(item=>{
    return Object.keys(item).some(key=>
      item[key].toString().toLowercase().includes(filter.toString().toLowerCase()))

  });


  return (
    <>
  
    <div className="col-12 mb-5">
      <div className="mb-3 col-4 mx-auto text-center">
        <label className="form-label h4">Find your movie</label>
        <input
        type="text"
        className="form-control"
        value={filter}
        onChange={searchText.bind(this)}
        />
      </div>
    </div>

      <section className="main-card--container">
        {dataSearch.map((curElem) => {
          const { id, name, category, image, description, watch } = curElem;
          

          

          return (
            <>
              <div className="card-container" key={id}>
                <div className="cardd ">
                  <div className="card-body">
                    <span className="card-number card-rectangle subtle">Id: {id}</span>
                    <span className="card-author"> {category}</span>
                    <h2 className="card-title"> {name} </h2>
                    <span className="card-description">
                      {description}
                    </span>

                    <div className="animate-border"></div><button onClick={handleClick} className="vote">{buttonText}</button>
                  </div>
                  <img src={image} alt="images" className="card-media" width="350px" height="200px" />

                  <a target="-blank" className="card-tag" href={watch}>Watch Now</a>
                </div>
              </div>
            </>
          );
        })}
      </section>
    </>
  );
};

export default MenuCard;
