import React, { useEffect, useState } from 'react';
import '../'

const APIURL = "https://api.themoviedb.org/3/discover/movie?api_key=04c35731a5ee918f014970082a0088b1";
const IMGPATH = "https://image.tmdb.org/t/p/w1280";
const SEARCHAPI =
    "https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query=";

const TrendingMovies = () => {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        // Initially get trending movies
        getMovies(APIURL);
    }, []);

    async function getMovies(url) {
        const resp = await fetch(url);
        const respData = await resp.json();

        console.log(respData);

        showMovies(respData.results);
    }

    function showMovies(movies) {
        setMovies(movies);
    }

    function getClassByRate(vote) {
        if (vote >= 8) {
            return "green";
        } else if (vote >= 5) {
            return "orange";
        } else {
            return "red";
        }
    }
    form.addEventListener("submit", (e) => {
        e.preventDefault();

        const searchTerm = search.value;

        if (searchTerm) {
            getMovies(SEARCHAPI + searchTerm);

            search.value = "";
        }
    });

    return (

        <div className="trending-movie-cards">
            <header className="trending-header">
                <p className="trending-movies">Trending Movies</p>
                <form id="form" className="form">
                    <input type="text" id="trending-search" placeholder="Search" className="trending-search" />
                </form>
            </header>
            <div className="trending-content">
                {movies.map((movie) => {
                    const { id, poster_path, title, vote_average, overview } = movie;

                    return (
                        <div key={id} className="trending-moviee">
                            <img
                                src={IMGPATH + poster_path}
                                alt={title}
                            />
                            <div className="trending-movie-info">
                                <h3>{title}</h3>
                                <span className={getClassByRate(vote_average)}>{vote_average}</span>
                            </div>
                            <div className="trending-overview">
                                <h3>Overview:</h3>
                                {overview}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default TrendingMovies;
