import React, { useEffect, useState } from 'react';
import Footer from './Footer';
import ScrollToTop from './GoUpBtn';
import './TrendingMovies.css';

const APIURL = "https://api.themoviedb.org/3/discover/movie?api_key=04c35731a5ee918f014970082a0088b1";
const IMGPATH = "https://image.tmdb.org/t/p/w1280";
const SEARCHAPI =
  "https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query=";

const TrendingMovies = () => {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // Initially get trending movies
    getMovies(APIURL);
  }, []);

  async function getMovies(url) {
    const resp = await fetch(url);
    const respData = await resp.json();
    showMovies(respData.results);
  }

  function showMovies(movies) {
    setMovies(movies);
  }

  function getClassByRate(vote) {
    if (vote >= 8) {
      return "green";
    } else if (vote >= 5) {
      return "orange";
    } else {
      return "red";
    }
  }

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleRefreshClick = async (e) => {
    e.preventDefault();
    if (search.trim() !== "") {
      const resp = await fetch(SEARCHAPI + search);
      const respData = await resp.json();
      setSearchResults(respData.results);
    } else {
      getMovies(APIURL);
      setSearchResults([]);
    }
  };

  const handleGoBack = () => {
    window.history.back();
  };

  return (
    <div className="trending-movie-cards">
      <header className="trending-header">
        <p className="trending-movies">Trending Movies</p>
        <div className="header-buttons">
          <button className="back-button" onClick={handleGoBack}>
            Go Back
          </button>
          <div className="search-form">
            <input
              type="text"
              id="search"
              placeholder="Search"
              value={search}
              onChange={handleSearchChange}
              className="search"
            />
            <button type="submit" className="refresh-button" onClick={handleRefreshClick}>
              Refresh
            </button>
          </div>
        </div>
      </header>
      <div className="trending-content">
        {search.trim() !== "" ? (
          searchResults.map((movie) => {
            const { id, poster_path, title, vote_average, overview } = movie;

            return (
              <div key={id} className="trending-moviee">
                <img src={IMGPATH + poster_path} alt={title} />
                <div className="trending-movie-info">
                  <h3>{title}</h3>
                  <span className={getClassByRate(vote_average)}>
                    {vote_average}
                  </span>
                </div>
                <div className="trending-overview">
                  <h3>Overview:</h3>
                  {overview}
                </div>
              </div>
            );
          })
        ) : (
          movies.map((movie) => {
            const { id, poster_path, title, vote_average, overview } = movie;

            return (
              <div key={id} className="trending-moviee">
                <img src={IMGPATH + poster_path} alt={title} />
                <div className="trending-movie-info">
                  <h3>{title}</h3>
                  <span className={getClassByRate(vote_average)}>
                    {vote_average}
                  </span>
                </div>
                <div className="trending-overview">
                  <h3>Overview:</h3>
                  {overview}
                </div>
              </div>
            );
          })
        )}
      </div>
      <Footer />
      <ScrollToTop />
    </div>
  );
};

export default TrendingMovies;
