import React, { useState } from "react";
import "./style.css";
import Menu from "./MovieApi";
import MenuCard from "./Menucard";
import Navbar from "./MovieNavbar";
import Footer from "./Footer";
import Imgslider from "./Carousel";
import ScrollToTop from "./GoUpBtn";
import Navbar2 from "./MainNavbar";

const uniqueBranchList = [
  ...new Set(
    Menu.map((curElem) => {
      return curElem.branch;
    })
  ),
  "All",
];

const uniqueSemesterList = [
  ...new Set(
    Menu.map((curElem) => {
      return curElem.semester;
    })
  ),
  "All",
];

const Boxoffice = () => {
  const [menuData, setMenuData] = useState(Menu);
  const [branchList] = useState(uniqueBranchList);
  const [semesterList] = useState(uniqueSemesterList);

  // Filter by Branch and Semester
  const filterItem = (filters) => {
    const { branch, semester } = filters;

    if (branch === "All" && semester === "All") {
      setMenuData(Menu);
      return;
    }

    const updatedList = Menu.filter((curElem) => {
      return (branch === "All" || curElem.branch === branch) &&
             (semester === "All" || curElem.semester === semester);
    });

    setMenuData(updatedList);
  };

  return (
    <>
      {/* Navbar at the top of the Movie section */}
      <Navbar2 />

      <div class="sliderr">Featured Movies</div>
      <div className="Sliderbg">
        {/* Carousel */}
        <Imgslider />
      </div>

      {/* Navbar for Filter */}
      <Navbar
        filterItem={filterItem}
        branchList={branchList}
        semesterList={semesterList}
      />

      <MenuCard menuData={menuData} />

      {/* Scroll to Top */}
      <ScrollToTop />

      <Footer />
    </>
  );
};

export default Boxoffice;
