import React, { useState } from "react";

const Navbar = ({ filterItem, menuList }) => {
  const [showDropdown, setShowDropdown] = useState(false);

  const handleToggleDropdown = () => {
    setShowDropdown((prevShowDropdown) => !prevShowDropdown);
  };

  const handleFilterItem = (category) => {
    filterItem(category);
    setShowDropdown(false); // Hide the dropdown after clicking a category
  };

  return (
    <>
      <div id="section1" className="line"></div>
      <nav className="navbar">
        <div className="nav-content">
          <div className="navbar-big-screen">
            {menuList.map((curElem) => (
              <button
                key={curElem}
                className="nav-btn"
                onClick={() => handleFilterItem(curElem)}
              >
                {curElem}
              </button>
            ))}
          </div>

          <div className="navbar-small-screen">
            <button className="explore-categories-btn" onClick={handleToggleDropdown}>
              Explore Categories
              <span className={`arrow ${showDropdown ? "up" : "down"}`}>&#9660;</span>
            </button>
            {showDropdown && (
              <div className="dropdown-menu">
                {menuList.map((curElem) => (
                  <button
                    key={curElem}
                    className="dropdown-btn"
                    onClick={() => handleFilterItem(curElem)}
                  >
                    {curElem}
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;
